package com.wipro.flowers.controller;import com.google.gson.Gson;import com.google.gson.reflect.TypeToken;import com.wipro.flowers.model.FlowersDTO;import com.wipro.flowers.service.impl.FlowersServiceImpl;import com.wipro.flowers.utils.CommanUtils;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.lang.reflect.Type;import java.util.List;@RestController@RequestMapping("/flowers")public class FlowersController {    @Autowired    private FlowersServiceImpl flowersServiceImpl;    @Autowired(required = true)    public void AdminController(FlowersServiceImpl flowersServiceImpl) {        this.flowersServiceImpl = flowersServiceImpl;    }    @PostMapping("/count")    public List<FlowersDTO> countUser(@RequestBody String flowersInput){        List<FlowersDTO> flowerList = CommanUtils.convertStringToObject(flowersInput);        return flowersServiceImpl.countUniqueUser(flowerList);    }    @PostMapping("/update")    public List<FlowersDTO> UpdateUser(@RequestBody String flowersInput){        List<FlowersDTO> flowerList = CommanUtils.convertStringToObject(flowersInput);        return flowersServiceImpl.updateUser(flowerList);    }}